import React, { Component } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './index.css';

class PostJob extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: '',
      company: '',
      location: '',
      salary: '',
      description: '',
      error: null
    };
  }

  handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'salary') {
      // Only allow empty string or positive integers
      if (value === '' || /^[0-9]+$/.test(value)) {
        this.setState({ 
          [name]: value,
          error: null
        });
      }
    } else {
      this.setState({ 
        [name]: value,
        error: null
      });
    }
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const { title, company, location, salary, description } = this.state;
    
    // Validation
    if (!title || !company || !location || !description) {
      this.setState({ error: "Please fill in all required fields" });
      toast.error("Please fill in all required fields");
      return;
    }

    // Create new job object
    const newJob = {
      id: Date.now(), // Unique ID
      title,
      company,
      location,
      salary: salary ? parseInt(salary) : null,
      description,
      postedAt: new Date().toISOString()
    };

    // Pass to parent component
    this.props.onJobPosted(newJob);

    // Clear form
    this.setState({
      title: '',
      company: '',
      location: '',
      salary: '',
      description: '',
      error: null
    });

    // Show success message
    toast.success("Job posted successfully!", {
      position: "top-right",
      autoClose: 3000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
    });
  };

  render() {
    const { title, company, location, salary, description, error } = this.state;

    return (
      <div className="post-job-container">
        <h2 className="form-title">Post a New Job</h2>
        {error && <div className="error-message">{error}</div>}
        <form onSubmit={this.handleSubmit} className="job-form">
          <input
            type="text"
            name="title"
            value={title}
            onChange={this.handleChange}
            placeholder="Job Title *"
            className="form-input"
            required
          />
          <input
            type="text"
            name="company"
            value={company}
            onChange={this.handleChange}
            placeholder="Company Name *"
            className="form-input"
            required
          />
          <input
            type="text"
            name="location"
            value={location}
            onChange={this.handleChange}
            placeholder="Location *"
            className="form-input"
            required
          />
          <input
            type="text"
            name="salary"
            value={salary}
            onChange={this.handleChange}
            placeholder="Salary (numbers only)"
            className="form-input"
            pattern="[0-9]*"
          />
          <textarea
            name="description"
            value={description}
            onChange={this.handleChange}
            placeholder="Job Description *"
            className="form-textarea"
            required
            rows="5"
          />
          <button type="submit" className="submit-button">
            Post Job
          </button>
        </form>
        <ToastContainer />
      </div>
    );
  }
}

export default PostJob;
