import React, { Component } from 'react';
import './index.css';

class Jobs extends Component {
  constructor(props) {
    super(props);
    this.state = {
      jobs: [],
      loading: true,
      error: null,
      searchTerm: '',
      locationFilter: '',
    };
  }

  componentDidMount() {
    this.loadJobsFromLocalStorage();
  }

  loadJobsFromLocalStorage = () => {
    try {
      const storedJobs = JSON.parse(localStorage.getItem('jobs')) || [];
      this.setState({ jobs: storedJobs, loading: false });
    } catch (error) {
      this.setState({ error: 'Failed to load jobs from local storage', loading: false });
    }
  };

  handleSearchChange = (e) => {
    this.setState({ searchTerm: e.target.value });
  };

  handleLocationChange = (e) => {
    this.setState({ locationFilter: e.target.value });
  };

  render() {
    const { jobs, loading, error, searchTerm, locationFilter } = this.state;

    const normalizedSearchTerm = searchTerm.trim().toLowerCase();
    const normalizedLocation = locationFilter.trim().toLowerCase();

    const filteredJobs = jobs.filter(job => {
      const matchesSearch =
        (job.title || '').toLowerCase().includes(normalizedSearchTerm) ||
        (job.company || '').toLowerCase().includes(normalizedSearchTerm) ||
        (job.description || '').toLowerCase().includes(normalizedSearchTerm);

      const matchesLocation =
        normalizedLocation === '' ||
        (job.location || '').toLowerCase().includes(normalizedLocation);

      return matchesSearch && matchesLocation;
    });

    if (loading) {
      return <div className="loading">Loading jobs...</div>;
    }

    if (error) {
      return <div className="error">Error: {error}</div>;
    }

    return (
      <div className="all-jobs-container">
        <div className="job-filters">
          <input
            type="text"
            placeholder="Search jobs, companies, or keywords"
            value={searchTerm}
            onChange={this.handleSearchChange}
            className="search-input"
          />
          <input
            type="text"
            placeholder="Filter by location"
            value={locationFilter}
            onChange={this.handleLocationChange}
            className="location-input"
          />
        </div>

        <div className="job-cards-container">
          {filteredJobs.length > 0 ? (
            filteredJobs.map((job, index) => (
              <div key={index} className="job-card">
                <div className="job-card-header">
                  <h3 className="job-title">{job.title}</h3>
                  <span className="company-name">{job.company}</span>
                </div>
                <div className="job-details">
                  <span className="job-location">
                    <i className="fas fa-map-marker-alt"></i> {job.location}
                  </span>
                  {job.salary && (
                    <span className="job-salary">
                      <i className="fas fa-money-bill-wave"></i> â‚¹{job.salary.toLocaleString()}/year
                    </span>
                  )}
                </div>
                <div className="job-description">
                  {job.description.length > 150
                    ? `${job.description.substring(0, 150)}...`
                    : job.description}
                </div>
                <button className="apply-button">Apply Now</button>
                <button className="save-button">
                  <i className="far fa-bookmark"></i> Save
                </button>
              </div>
            ))
          ) : (
            <div className="no-jobs">No jobs found matching your criteria</div>
          )}
        </div>
      </div>
    );
  }
}

export default Jobs;
