import React, { Component } from 'react';
import PostJob from '../PostJob';
import './index.css';

class Jobs extends Component {
  constructor(props) {
    super(props);
    this.state = {
      jobs: [], // Initial empty array or you can add some mock data
      loading: false, // No loading needed since we're not making API calls
      error: null,
      searchTerm: '',
      locationFilter: '',
      showPostJobForm: false
    };
  }

  // Add a new job to the list
  handleJobPosted = (newJob) => {
    this.setState(prevState => ({
      jobs: [...prevState.jobs, {
        ...newJob,
        id: Date.now() // Generate a unique ID (timestamp)
      }],
      showPostJobForm: false // Optionally hide the form after submission
    }));
  };

  handleSearchChange = (e) => {
    this.setState({ searchTerm: e.target.value });
  };

  handleLocationChange = (e) => {
    this.setState({ locationFilter: e.target.value });
  };

  togglePostJobForm = () => {
    this.setState(prevState => ({ showPostJobForm: !prevState.showPostJobForm }));
  };

  render() {
    const { 
      jobs, 
      error, 
      searchTerm, 
      locationFilter,
      showPostJobForm
    } = this.state;

    // Filter jobs based on search criteria
    const filteredJobs = jobs.filter(job => {
      const matchesSearch = job.title.toLowerCase().includes(searchTerm.toLowerCase()) || 
                          job.company.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          job.description.toLowerCase().includes(searchTerm.toLowerCase());
      
      const matchesLocation = locationFilter === '' || 
                            job.location.toLowerCase().includes(locationFilter.toLowerCase());
      
      return matchesSearch && matchesLocation;
    });

    if (error) {
      return <div className="error">Error: {error}</div>;
    }

    return (
      <div className="jobs-page-container">
        <div className="job-actions">
          <button onClick={this.togglePostJobForm} className="post-job-button">
            {showPostJobForm ? 'Hide Post Job Form' : 'Post a New Job'}
          </button>
        </div>

        {showPostJobForm && (
          <PostJob onJobPosted={this.handleJobPosted} />
        )}

        <div className="all-jobs-container">
          <div className="job-filters">
            <input
              type="text"
              placeholder="Search jobs, companies, or keywords"
              value={searchTerm}
              onChange={this.handleSearchChange}
              className="search-input"
            />
            <input
              type="text"
              placeholder="Filter by location"
              value={locationFilter}
              onChange={this.handleLocationChange}
              className="location-input"
            />
          </div>

          <div className="job-cards-container">
            {filteredJobs.length > 0 ? (
              filteredJobs.map(job => (
                <div key={job.id} className="job-card">
                  <div className="job-card-header">
                    <h3 className="job-title">{job.title}</h3>
                    <span className="company-name">{job.company}</span>
                  </div>
                  <div className="job-details">
                    <span className="job-location">
                      <i className="fas fa-map-marker-alt"></i> {job.location}
                    </span>
                    {job.salary && (
                      <span className="job-salary">
                        <i className="fas fa-money-bill-wave"></i> ₹{job.salary.toLocaleString()}/year
                      </span>
                    )}
                  </div>
                  <div className="job-description">
                    {job.description.length > 150 
                      ? `${job.description.substring(0, 150)}...` 
                      : job.description}
                  </div>
                  <button className="apply-button">Apply Now</button>
                  <button className="save-button">
                    <i className="far fa-bookmark"></i> Save
                  </button>
                </div>
              ))
            ) : (
              <div className="no-jobs">No jobs found matching your criteria</div>
            )}
          </div>
        </div>
      </div>
    );
  }
}

export default Jobs;
