// PostJob.js
import React, { Component } from 'react';
import './index.css'

class PostJob extends Component {
  state = {
    title: '',
    description: '',
    location: '',
  };

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const { title, description, location } = this.state;
    const newJob = {
      id: Date.now(),
      title,
      description,
      location,
    };

    // Call the parent component's function
    this.props.onJobPosted(newJob);

    // Clear the form
    this.setState({ title: '', description: '', location: '' });
  };

  render() {
    const { title, description, location } = this.state;

    return (
      <form onSubmit={this.handleSubmit}>
        <h3>Post a Job</h3>
        <input
          type="text"
          name="title"
          value={title}
          onChange={this.handleChange}
          placeholder="Job Title"
          required
        />
        <br />
        <textarea
          name="description"
          value={description}
          onChange={this.handleChange}
          placeholder="Job Description"
          required
        />
        <br />
        <input
          type="text"
          name="location"
          value={location}
          onChange={this.handleChange}
          placeholder="Location"
          required
        />
        <br />
        <button type="submit">Post Job</button>
      </form>
    );
  }
}

export default PostJob;
